INSERT INTO articles (id, title, slug, description, content, author_id, publish_date, is_published) VALUES (1, 'Understanding JavaScript Closures', 'understanding-javascript-closures', 'A deep dive into closures in JavaScript.', 'Closures are a fundamental concept in JavaScript. They allow functions to access variables from an enclosing scope, even after that scope has closed. This article explores how closures work and common use cases.', 1, '2025-08-01', true), (2, 'Getting Started with React', 'getting-started-with-react', 'Beginner guide to building apps with React.', 'React is a popular JavaScript library for building user interfaces. This article covers the basics of React components, state, and props to help you get started.', 2, '2025-08-03', true), (3, 'Mastering CSS Flexbox', 'mastering-css-flexbox', 'Tips and tricks for using Flexbox in CSS.', 'Flexbox is a powerful layout module in CSS. Learn how to use Flexbox to create responsive layouts and align items easily.', 3, '2025-08-05', true), (4, 'Introduction to TypeScript', 'introduction-to-typescript', 'Why TypeScript is a great choice for JavaScript developers.', 'TypeScript adds static typing to JavaScript, helping you catch errors early and write more maintainable code. This article introduces TypeScript features and setup.', 4, '2025-08-07', true), (5, 'Building REST APIs with Node.js', 'building-rest-apis-with-nodejs', 'Step-by-step guide to creating RESTful APIs.', 'Node.js makes it easy to build scalable REST APIs. This article walks you through setting up Express and creating endpoints.', 5, '2025-08-09', true), (6, 'Async Programming in Python', 'async-programming-in-python', 'Learn about async and await in Python.', 'Async programming allows you to write concurrent code using async and await. Discover how to use Pythonâ€™s asyncio library for efficient I/O operations.', 1, '2025-08-11', true), (7, 'Deploying Apps with Docker', 'deploying-apps-with-docker', 'A practical guide to containerizing applications.', 'Docker simplifies deployment by packaging your app and its dependencies into containers. Learn how to create Dockerfiles and manage containers.', 2, '2025-08-13', true), (8, 'Effective Git Workflows', 'effective-git-workflows', 'Best practices for collaborating with Git.', 'Git is essential for version control. This article covers branching strategies, pull requests, and resolving merge conflicts.', 3, '2025-08-15', true), (9, 'Responsive Web Design Basics', 'responsive-web-design-basics', 'How to make your website look great on any device.', 'Responsive design ensures your website works on all screen sizes. Learn about media queries, flexible grids, and mobile-first approaches.', 4, '2025-08-17', true), (10, 'Introduction to SQL Databases', 'introduction-to-sql-databases', 'Getting started with relational databases.', 'SQL databases store data in tables and support powerful queries. This article introduces basic SQL commands and database design principles.', 5, '2025-08-19', true);